import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

export const StroopTemplate = ({ onComplete }) => {
  const [currentTrial, setCurrentTrial] = useState(0);
  const [word, setWord] = useState('');
  const [color, setColor] = useState('');
  const [startTime, setStartTime] = useState(0);
  const [results, setResults] = useState([]);
  const [isTraining, setIsTraining] = useState(true);

  const words = ['RED', 'GREEN', 'BLUE', 'YELLOW'];
  const colors = { RED: 'red', GREEN: 'green', BLUE: 'blue', YELLOW: 'yellow' };
  const keys = { r: 'RED', g: 'GREEN', b: 'BLUE', y: 'YELLOW' };

  useEffect(() => {
    generateTrial();
  }, []);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (keys[e.key.toLowerCase()]) {
        handleResponse(keys[e.key.toLowerCase()]);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [word, color, startTime]);

  const generateTrial = () => {
    const newWord = words[Math.floor(Math.random() * words.length)];
    const newColor = words[Math.floor(Math.random() * words.length)];
    setWord(newWord);
    setColor(newColor);
    setStartTime(Date.now());
  };

  const handleResponse = (response) => {
    const rt = Date.now() - startTime;
    const correct = response === color;
    const compatible = word === color ? 1 : 0;
    const result = { word, color, compatible, response, correct: correct ? 1 : 0, rt };
    setResults([...results, result]);

    const nextTrial = currentTrial + 1;
    if (isTraining && nextTrial >= 5) {
      setIsTraining(false);
      setCurrentTrial(0);
      generateTrial();
    } else if (!isTraining && nextTrial >= 20) {
      onComplete?.(results);
    } else {
      setCurrentTrial(nextTrial);
      generateTrial();
    }
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle>Stroop Task</CardTitle>
        <CardDescription>{isTraining ? 'Training Mode' : `Trial ${currentTrial + 1} of 20`}</CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="text-center text-sm text-muted-foreground">Press the key corresponding to the COLOR of the word (not what it says)</div>
        <div className="flex justify-center items-center h-64">
          <div className="text-6xl font-bold" style={{ color: colors[color] }}>{word}</div>
        </div>
        <div className="grid grid-cols-4 gap-2">
          {Object.entries(colors).map(([name, colorValue]) => (
            <Button key={name} onClick={() => handleResponse(name)} style={{ backgroundColor: colorValue }} className="text-white">{name[0]}</Button>
          ))}
        </div>
        <div className="text-center text-xs text-muted-foreground">Keyboard shortcuts: R, G, B, Y</div>
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

export const BARTTemplate = ({ onComplete }) => {
  const [balloonSize, setBalloonSize] = useState(50);
  const [pumps, setPumps] = useState(0);
  const [earnings, setEarnings] = useState(0);
  const [currentEarnings, setCurrentEarnings] = useState(0);
  const [balloonColor, setBalloonColor] = useState('blue');
  const [balloonCount, setBalloonCount] = useState(1);
  const [exploded, setExploded] = useState(false);
  const [results, setResults] = useState([]);
  const [isTraining, setIsTraining] = useState(true);
  const [maxPumps, setMaxPumps] = useState(10);

  const balloonColors = { blue: { color: '#3B82F6', maxAvg: 64 }, yellow: { color: '#EAB308', maxAvg: 16 }, orange: { color: '#F97316', maxAvg: 4 }, green: { color: '#22C55E', maxAvg: 10 } };

  useEffect(() => { resetBalloon(); }, []);

  const resetBalloon = () => {
    const colors = isTraining ? ['green'] : ['blue', 'yellow', 'orange'];
    const newColor = colors[Math.floor(Math.random() * colors.length)];
    const avgMax = balloonColors[newColor].maxAvg;
    const newMax = Math.floor(avgMax * (0.5 + Math.random()));
    setBalloonColor(newColor);
    setMaxPumps(newMax);
    setBalloonSize(50);
    setPumps(0);
    setCurrentEarnings(0);
    setExploded(false);
  };

  const handlePump = () => {
    if (exploded) return;
    const newPumps = pumps + 1;
    const newEarnings = currentEarnings + 0.05;
    setPumps(newPumps);
    setCurrentEarnings(newEarnings);
    setBalloonSize(50 + newPumps * 10);
    if (newPumps >= maxPumps) {
      setExploded(true);
      setResults([...results, { balloonColor, balloonCount, timesPumped: newPumps, explosion: 1, earningsThisBalloon: 0, totalEarnings: earnings }]);
      setTimeout(() => {
        if (balloonCount >= 33) { onComplete?.(results); } else { setBalloonCount(balloonCount + 1); if (isTraining && balloonCount >= 3) { setIsTraining(false); } resetBalloon(); }
      }, 1000);
    }
  };

  const handleCollect = () => {
    if (exploded) return;
    const newEarnings = earnings + currentEarnings;
    setEarnings(newEarnings);
    setResults([...results, { balloonColor, balloonCount, timesPumped: pumps, explosion: 0, earningsThisBalloon: currentEarnings, totalEarnings: newEarnings }]);
    if (balloonCount >= 33) { onComplete?.(results); } else { setBalloonCount(balloonCount + 1); if (isTraining && balloonCount >= 3) { setIsTraining(false); } resetBalloon(); }
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader><CardTitle>Balloon Analogue Risk Task (BART)</CardTitle><CardDescription>{isTraining ? 'Training Mode' : `Trial ${balloonCount - 3} of 30`}</CardDescription></CardHeader>
      <CardContent className="space-y-4">
        <div className="flex justify-between text-sm"><div>Current Trial: ${currentEarnings.toFixed(2)}</div><div>Total Earnings: ${earnings.toFixed(2)}</div></div>
        <div className="flex justify-center items-center h-96 relative"><div className="rounded-full transition-all duration-200" style={{ width: `${balloonSize}px`, height: `${balloonSize}px`, backgroundColor: exploded ? '#EF4444' : balloonColors[balloonColor].color, opacity: exploded ? 0.5 : 1 }}>{exploded && <div className="flex items-center justify-center h-full text-white font-bold text-2xl">ðŸ’¥ POP!</div>}</div></div>
        <div className="flex gap-4 justify-center"><Button onClick={handlePump} disabled={exploded} size="lg">Pump (+$0.05)</Button><Button onClick={handleCollect} disabled={exploded} variant="outline" size="lg">Collect Money</Button></div>
        <div className="text-center text-sm text-muted-foreground">Pumps: {pumps}</div>
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

export const FlankerTemplate = ({ onComplete }) => {
  const [currentTrial, setCurrentTrial] = useState(0);
  const [stimulus, setStimulus] = useState('');
  const [congruent, setCongruent] = useState(true);
  const [targetLetter, setTargetLetter] = useState('');
  const [phase, setPhase] = useState('fixation');
  const [startTime, setStartTime] = useState(0);
  const [results, setResults] = useState([]);
  const [isTraining, setIsTraining] = useState(true);
  const [feedback, setFeedback] = useState('');
  const [showReminder, setShowReminder] = useState(false);

  const leftLetters = ['X', 'C'];
  const rightLetters = ['V', 'B'];
  const allLetters = [...leftLetters, ...rightLetters];

  useEffect(() => { startTrial(); }, [currentTrial]);
  useEffect(() => {
    const handleKeyPress = (e) => { if (phase === 'stimulus' && (e.key === 'a' || e.key === 'l')) { handleResponse(e.key); } };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [phase, stimulus, startTime]);

  const startTrial = () => {
    const totalTrials = isTraining ? 5 : 20;
    if (currentTrial >= totalTrials) { if (isTraining) { setIsTraining(false); setCurrentTrial(0); return; } else { onComplete?.(results); return; } }
    setPhase('fixation');
    setFeedback('');
    const isCongruent = Math.random() < 0.5;
    const target = allLetters[Math.floor(Math.random() * allLetters.length)];
    let flankers = isCongruent ? target : (leftLetters.includes(target) ? leftLetters : rightLetters).find(l => l !== target) || target;
    const stimulusString = `${flankers}${flankers}${target}${flankers}${flankers}`;
    setStimulus(stimulusString);
    setCongruent(isCongruent);
    setTargetLetter(target);
    setTimeout(() => { setPhase('stimulus'); setStartTime(Date.now()); setTimeout(() => { if (phase === 'stimulus') { handleResponse(null); } }, 2000); }, 500);
  };

  const handleResponse = (key) => {
    if (phase !== 'stimulus') return;
    const rt = Date.now() - startTime;
    const correctKey = leftLetters.includes(targetLetter) ? 'a' : 'l';
    const correct = key === correctKey;
    const status = !key ? 3 : (correct ? 1 : 2);
    const result = { stimulus, congruent: congruent ? 1 : 0, targetLetter, response: key || 'timeout', correct: correct ? 1 : 0, status, rt: key ? rt : 2000 };
    setResults([...results, result]);
    setPhase('feedback');
    if (status === 1) { setFeedback('âœ“ Correct'); } else if (status === 2) { setFeedback('âœ— Error - Remember the rules!'); setShowReminder(true); setTimeout(() => setShowReminder(false), 2000); } else { setFeedback('Too slow!'); }
    setTimeout(() => { setCurrentTrial(currentTrial + 1); }, status === 2 ? 2500 : 800);
  };

  const getInstructions = () => (
    <div className="space-y-2 text-sm"><p className="font-semibold">Press the key for the MIDDLE letter:</p><div className="grid grid-cols-2 gap-2 text-xs"><div className="bg-blue-100 dark:bg-blue-900 p-2 rounded"><strong>A key (Left):</strong> X or C</div><div className="bg-green-100 dark:bg-green-900 p-2 rounded"><strong>L key (Right):</strong> V or B</div></div><p className="text-xs text-muted-foreground">Ignore the flanking letters!</p></div>
  );

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader><CardTitle>Flanker Task (Eriksen)</CardTitle><CardDescription>{isTraining ? `Training Trial ${currentTrial + 1} of 5` : `Trial ${currentTrial + 1} of 20`}</CardDescription></CardHeader>
      <CardContent className="space-y-6">
        {getInstructions()}
        <div className="flex flex-col items-center justify-center h-64 relative">
          {phase === 'fixation' && <div className="text-6xl font-bold">+</div>}
          {phase === 'stimulus' && <div className="space-y-4 text-center"><div className="text-6xl font-mono font-bold tracking-wider">{stimulus}</div><div className="text-xs text-muted-foreground">Respond to the middle letter!</div></div>}
          {phase === 'feedback' && feedback && <div className={`text-2xl font-bold ${feedback.includes('âœ“') ? 'text-green-600' : 'text-red-600'}`}>{feedback}</div>}
        </div>
        {showReminder && <div className="bg-yellow-100 dark:bg-yellow-900 border border-yellow-400 rounded p-3">{getInstructions()}</div>}
        <div className="text-center text-xs text-muted-foreground">{isTraining ? 'ðŸŽ“ Training Mode - Practice makes perfect!' : 'ðŸ“Š Main Experiment'}</div>
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

export const PosnerTemplate = ({ onComplete }) => {
  const [currentTrial, setCurrentTrial] = useState(0);
  const [phase, setPhase] = useState('fixation');
  const [cuePosition, setCuePosition] = useState('left');
  const [targetPosition, setTargetPosition] = useState('left');
  const [startTime, setStartTime] = useState(0);
  const [results, setResults] = useState([]);

  useEffect(() => { startTrial(); }, [currentTrial]);
  useEffect(() => {
    const handleKeyPress = (e) => { if (phase === 'target' && (e.key === 'a' || e.key === 'l')) { handleResponse(e.key === 'a' ? 'left' : 'right'); } };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [phase, startTime]);

  const startTrial = () => {
    if (currentTrial >= 40) { onComplete?.(results); return; }
    const isValidCue = Math.random() < 0.75;
    const cue = Math.random() < 0.5 ? 'left' : 'right';
    const target = isValidCue ? cue : (cue === 'left' ? 'right' : 'left');
    setCuePosition(cue);
    setTargetPosition(target);
    setPhase('fixation');
    setTimeout(() => { setPhase('cue'); setTimeout(() => { setPhase('target'); setStartTime(Date.now()); }, 150); }, 1000);
  };

  const handleResponse = (response) => {
    const rt = Date.now() - startTime;
    const correct = response === targetPosition;
    const valid = cuePosition === targetPosition ? 1 : 0;
    setResults([...results, { cuePosition, targetPosition, valid, response, correct: correct ? 1 : 0, rt }]);
    setCurrentTrial(currentTrial + 1);
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader><CardTitle>Posner Cueing Task</CardTitle><CardDescription>Trial {currentTrial + 1} of 40</CardDescription></CardHeader>
      <CardContent>
        <div className="text-center text-sm text-muted-foreground mb-6">Press 'A' for left box, 'L' for right box when you see GO</div>
        <div className="flex justify-center gap-8 h-64 items-center">
          <div className="w-32 h-32 border-4 border-yellow-400 relative flex items-center justify-center">{phase === 'cue' && cuePosition === 'left' && <div className="text-4xl font-bold">X</div>}{phase === 'target' && targetPosition === 'left' && <div className="w-24 h-24 rounded-full bg-green-500 flex items-center justify-center text-white font-bold">GO</div>}</div>
          <div className="w-32 h-32 border-4 border-yellow-400 relative flex items-center justify-center">{phase === 'cue' && cuePosition === 'right' && <div className="text-4xl font-bold">X</div>}{phase === 'target' && targetPosition === 'right' && <div className="w-24 h-24 rounded-full bg-green-500 flex items-center justify-center text-white font-bold">GO</div>}</div>
        </div>
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

export const GoNoGoTemplate = ({ onComplete }) => {
  const [currentTrial, setCurrentTrial] = useState(0);
  const [trialType, setTrialType] = useState('go');
  const [phase, setPhase] = useState('fixation');
  const [startTime, setStartTime] = useState(0);
  const [results, setResults] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [responded, setResponded] = useState(false);

  const totalTrials = 30;

  useEffect(() => { startTrial(); }, [currentTrial]);
  useEffect(() => {
    const handleKeyPress = (e) => { if (phase === 'stimulus' && e.key === ' ' && !responded) { setResponded(true); handleResponse(); } };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [phase, responded]);

  const startTrial = () => {
    if (currentTrial >= totalTrials) { onComplete?.(results); return; }
    setPhase('fixation');
    setFeedback('');
    setResponded(false);
    const isGoTrial = Math.random() < 0.8;
    setTrialType(isGoTrial ? 'go' : 'nogo');
    setTimeout(() => { setPhase('stimulus'); setStartTime(Date.now()); setTimeout(() => { if (!responded) { handleTimeout(); } }, 2000); }, 500 + Math.random() * 500);
  };

  const handleResponse = () => {
    const rt = Date.now() - startTime;
    const isError = trialType === 'nogo';
    const result = { trialType, rt, error: isError ? 1 : 0, status: isError ? 2 : 1 };
    setResults([...results, result]);
    setPhase('feedback');
    if (isError) { setFeedback('âœ— Error - You should NOT press on No-Go!'); } else { setFeedback('âœ“ Correct - Good reaction!'); }
    setTimeout(() => { setCurrentTrial(currentTrial + 1); }, 600);
  };

  const handleTimeout = () => {
    const isError = trialType === 'go';
    const result = { trialType, rt: 2000, error: isError ? 1 : 0, status: isError ? 3 : 1 };
    setResults([...results, result]);
    setPhase('feedback');
    if (isError) { setFeedback('âœ— Too Slow - Press quickly on Go!'); } else { setFeedback('âœ“ Correct - Well done withholding!'); }
    setTimeout(() => { setCurrentTrial(currentTrial + 1); }, 600);
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader><CardTitle>Go/No-Go Task</CardTitle><CardDescription>Trial {currentTrial + 1} of {totalTrials}</CardDescription></CardHeader>
      <CardContent className="space-y-6">
        <div className="text-center text-sm bg-blue-100 dark:bg-blue-900 p-3 rounded"><p className="font-semibold">Instructions:</p><p>Press <kbd className="px-2 py-1 bg-white dark:bg-gray-800 rounded border">SPACEBAR</kbd> when you see <strong className="text-green-600">GO</strong></p><p>Do NOT press anything when you see <strong className="text-red-600">NO-GO</strong></p></div>
        <div className="flex flex-col items-center justify-center h-72 relative">
          {phase === 'fixation' && <div className="text-6xl font-bold text-muted-foreground">+</div>}
          {phase === 'stimulus' && <div className={`text-7xl font-bold px-8 py-4 rounded-lg ${trialType === 'go' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'}`}>{trialType === 'go' ? 'GO' : 'NO-GO'}</div>}
          {phase === 'feedback' && feedback && <div className={`text-xl font-bold ${feedback.includes('âœ“') ? 'text-green-600' : 'text-red-600'}`}>{feedback}</div>}
        </div>
        <div className="grid grid-cols-3 gap-4 text-center text-sm"><div><div className="text-2xl font-bold text-green-600">{results.filter(r => r.trialType === 'go' && r.status === 1).length}</div><div className="text-xs text-muted-foreground">Go Correct</div></div><div><div className="text-2xl font-bold text-blue-600">{results.filter(r => r.trialType === 'nogo' && r.status === 1).length}</div><div className="text-xs text-muted-foreground">No-Go Correct</div></div><div><div className="text-2xl font-bold text-red-600">{results.filter(r => r.error === 1).length}</div><div className="text-xs text-muted-foreground">Total Errors</div></div></div>
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';

export const NBackTemplate = ({ onComplete }) => {
  const [currentBlock, setCurrentBlock] = useState(1);
  const [currentTrial, setCurrentTrial] = useState(0);
  const [letter, setLetter] = useState('');
  const [sequence, setSequence] = useState([]);
  const [phase, setPhase] = useState('ready');
  const [startTime, setStartTime] = useState(0);
  const [results, setResults] = useState([]);
  const [responded, setResponded] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [blockStats, setBlockStats] = useState({ hits: 0, misses: 0, falseAlarms: 0, correctRejections: 0 });

  const letters = ['A', 'B', 'C', 'D', 'E', 'H', 'I', 'K', 'L', 'M', 'O', 'P', 'R', 'S', 'T'];
  const trialsPerBlock = 15;
  const totalBlocks = 2;
  const nBack = 2;

  useEffect(() => {
    const handleKeyPress = (e) => { if (phase === 'letter' && e.key === 'm' && !responded) { setResponded(true); handleResponse(true); } };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [phase, responded, sequence]);

  const startBlock = () => { setCurrentTrial(0); setSequence([]); setBlockStats({ hits: 0, misses: 0, falseAlarms: 0, correctRejections: 0 }); setPhase('letter'); startTrial(); };

  const startTrial = () => {
    if (currentTrial >= trialsPerBlock) { endBlock(); return; }
    setResponded(false);
    setFeedback('');
    let newLetter;
    const isMatch = currentTrial >= nBack && Math.random() < 0.3;
    if (isMatch) { newLetter = sequence[sequence.length - nBack]; } else { do { newLetter = letters[Math.floor(Math.random() * letters.length)]; } while (currentTrial >= nBack && newLetter === sequence[sequence.length - nBack]); }
    setLetter(newLetter);
    const newSequence = [...sequence, newLetter];
    setSequence(newSequence);
    setPhase('letter');
    setStartTime(Date.now());
    setTimeout(() => { setPhase('blank'); setTimeout(() => { if (!responded) { handleResponse(false); } }, 2500); }, 500);
  };

  const handleResponse = (didRespond) => {
    const rt = Date.now() - startTime;
    const isMatchTrial = currentTrial >= nBack && sequence[sequence.length - 1] === sequence[sequence.length - 1 - nBack];
    let isCorrect, resultType;
    if (isMatchTrial && didRespond) { isCorrect = true; resultType = 'hit'; setBlockStats(prev => ({ ...prev, hits: prev.hits + 1 })); } else if (isMatchTrial && !didRespond) { isCorrect = false; resultType = 'miss'; setBlockStats(prev => ({ ...prev, misses: prev.misses + 1 })); } else if (!isMatchTrial && didRespond) { isCorrect = false; resultType = 'falseAlarm'; setBlockStats(prev => ({ ...prev, falseAlarms: prev.falseAlarms + 1 })); } else { isCorrect = true; resultType = 'correctRejection'; setBlockStats(prev => ({ ...prev, correctRejections: prev.correctRejections + 1 })); }
    const result = { block: currentBlock, trial: currentTrial + 1, letter: letter, isMatch: isMatchTrial ? 1 : 0, responded: didRespond ? 1 : 0, correct: isCorrect ? 1 : 0, resultType, rt: didRespond ? rt : 3000 };
    setResults([...results, result]);
    if (didRespond) { setFeedback(isCorrect ? 'âœ“' : 'âœ—'); }
    setTimeout(() => { setCurrentTrial(currentTrial + 1); startTrial(); }, 200);
  };

  const endBlock = () => { setPhase('blockEnd'); };
  const nextBlock = () => { if (currentBlock >= totalBlocks) { onComplete?.(results); } else { setCurrentBlock(currentBlock + 1); setPhase('ready'); } };

  if (phase === 'ready') {
    return (
      <Card className="w-full max-w-2xl mx-auto"><CardHeader><CardTitle>N-Back Task (2-Back)</CardTitle><CardDescription>Block {currentBlock} of {totalBlocks}</CardDescription></CardHeader><CardContent className="space-y-6"><div className="bg-blue-100 dark:bg-blue-900 p-4 rounded space-y-3"><p className="font-semibold text-lg">Instructions:</p><p>You will see a sequence of letters, one at a time.</p><p>Press <kbd className="px-2 py-1 bg-white dark:bg-gray-800 rounded border font-bold">M</kbd> if the current letter is the SAME as the letter shown <strong>2 positions back</strong>.</p><p>Otherwise, do NOT press anything.</p><div className="text-sm text-muted-foreground mt-4"><p>Example: If you see: H â†’ K â†’ <strong>H</strong></p><p>Press M on the second H because it matches 2 back!</p></div></div><Button onClick={startBlock} size="lg" className="w-full">Start Block {currentBlock}</Button></CardContent></Card>
    );
  }

  if (phase === 'blockEnd') {
    const accuracy = blockStats.hits + blockStats.correctRejections;
    const total = trialsPerBlock;
    const percentage = ((accuracy / total) * 100).toFixed(1);
    return (
      <Card className="w-full max-w-2xl mx-auto"><CardHeader><CardTitle>Block {currentBlock} Complete!</CardTitle><CardDescription>Your Performance</CardDescription></CardHeader><CardContent className="space-y-6"><div className="grid grid-cols-2 gap-4"><div className="bg-green-100 dark:bg-green-900 p-4 rounded text-center"><div className="text-3xl font-bold text-green-600">{blockStats.hits}</div><div className="text-sm">Hits</div></div><div className="bg-blue-100 dark:bg-blue-900 p-4 rounded text-center"><div className="text-3xl font-bold text-blue-600">{blockStats.correctRejections}</div><div className="text-sm">Correct Rejections</div></div><div className="bg-yellow-100 dark:bg-yellow-900 p-4 rounded text-center"><div className="text-3xl font-bold text-yellow-600">{blockStats.misses}</div><div className="text-sm">Misses</div></div><div className="bg-red-100 dark:bg-red-900 p-4 rounded text-center"><div className="text-3xl font-bold text-red-600">{blockStats.falseAlarms}</div><div className="text-sm">False Alarms</div></div></div><div className="text-center p-4 bg-muted rounded"><div className="text-4xl font-bold">{percentage}%</div><div className="text-sm text-muted-foreground">Accuracy</div></div><Button onClick={nextBlock} size="lg" className="w-full">{currentBlock >= totalBlocks ? 'View Results' : `Continue to Block ${currentBlock + 1}`}</Button></CardContent></Card>
    );
  }

  return (
    <Card className="w-full max-w-2xl mx-auto"><CardHeader><CardTitle>N-Back Task (2-Back)</CardTitle><CardDescription>Block {currentBlock} of {totalBlocks} - Trial {currentTrial + 1} of {trialsPerBlock}</CardDescription></CardHeader><CardContent className="space-y-6"><div className="text-center text-sm text-muted-foreground">Press <kbd className="px-2 py-1 bg-muted rounded border">M</kbd> if the letter matches 2 back</div><div className="flex flex-col items-center justify-center h-80 relative">{phase === 'letter' && <div className="text-9xl font-bold">{letter}</div>}{phase === 'blank' && feedback && <div className={`text-6xl font-bold ${feedback === 'âœ“' ? 'text-green-600' : 'text-red-600'}`}>{feedback}</div>}{phase === 'blank' && !feedback && <div className="text-6xl font-bold text-muted-foreground opacity-20">â€¢</div>}</div><div className="grid grid-cols-4 gap-2 text-center text-xs"><div><div className="font-bold text-green-600">{blockStats.hits}</div><div className="text-muted-foreground">Hits</div></div><div><div className="font-bold text-blue-600">{blockStats.correctRejections}</div><div className="text-muted-foreground">Correct</div></div><div><div className="font-bold text-yellow-600">{blockStats.misses}</div><div className="text-muted-foreground">Misses</div></div><div><div className="font-bold text-red-600">{blockStats.falseAlarms}</div><div className="text-muted-foreground">False Alarms</div></div></div></CardContent></Card>
  );
};
